import{_ as o,c as l,o as e,a3 as i}from"./chunks/framework.DpzXWsSh.js";const h=JSON.parse('{"title":"小程序学习笔记","description":"","frontmatter":{"title":"小程序学习笔记","date":"2023-4-28","categories":["小程序","笔记"]},"headers":[],"relativePath":"blogs/note/xiaochengxu.md","filePath":"blogs/note/xiaochengxu.md"}'),t={name:"blogs/note/xiaochengxu.md"},c=i('<h2 id="小程序学习笔记" tabindex="-1">小程序学习笔记 <a class="header-anchor" href="#小程序学习笔记" aria-label="Permalink to &quot;小程序学习笔记&quot;">​</a></h2><h3 id="小程序简介" tabindex="-1"><strong>小程序简介</strong> <a class="header-anchor" href="#小程序简介" aria-label="Permalink to &quot;**小程序简介**&quot;">​</a></h3><ol><li>小程序与普通网页开发的区别 <ul><li>运行环境：一个是在<strong>浏览器环境</strong>，一个是<strong>微信环境</strong></li><li>API:小程序<strong>无法调用DOM和BOM的API</strong>，但是有微信环境对应的API，比如<strong>地理定位、扫码、支付</strong>等</li><li>开发模式:申请小程序开发账号、安装小程序开发者工具、创建和配置小程序项目</li></ul></li><li>项目的基本组成结构 <ul><li><code>pages</code>：<strong>存放所有小程序的页面</strong></li><li><code>utils</code>：存放工具性质的模块</li><li><code>app.js</code>：<strong>小程序项目的入口文件</strong></li><li><code>app.json</code>：<strong>小程序项目的全局配置文件</strong><ul><li><code>pages</code>：所有页面的路径</li><li><code>window</code>：全局定义所有页面的背景色、文字颜色等</li><li><code>style</code>：全局定义小程序组件所使用的样式版本</li><li><code>sitemapLocation</code>：用来指明<code>siemap.json</code>的位置</li></ul></li><li><code>app.wxss</code>：小程序项目的全局样式文件</li><li><code>project.config.json</code>：项目的配置文件 <ul><li><code>setting</code>：保存了编译相关的配置</li><li><code>projectname</code>：保存的是项目名称</li><li><code>appid</code>：保存的是小程序的账号ID</li></ul></li><li><code>sitemap.json</code>：配置小程序及其页面是否允许被微信索引，效果类似于PC网页的SEO</li></ul></li><li><code>WXML</code>和<code>HTML</code>和区别 <ol><li>标签名称不同 <ul><li><code>HTML(div,span,img,a)</code></li><li><code>WXML(view,text,image,navigator)</code></li></ul></li><li>属性节点不同 <ul><li><code>&lt;a href=&#39;#&#39;&gt;&lt;/a&gt;</code></li><li><code>&lt;navigator url=&#39;/pages/home/home&#39;&gt;&lt;/navigator&gt;</code></li></ul></li><li>提供了类似vue的模板语法 <ul><li>数据绑定</li><li>列表渲染</li><li>条件渲染</li></ul></li></ol></li><li><code>WXSS</code>和<code>css</code>的区别 <ol><li>新增了rpx尺寸单位 <ul><li>css中需要手动进行像素单位换算，例如rem</li><li>wxss在底层支持rpx，在不同大小的屏幕上小程序会自动进行换算</li></ul></li><li>提供了全局样式（app.wxss）和局部样式（.wxss）</li><li>支持部分CSS选择器</li></ol></li></ol>',3),a=[c];function s(d,n,r,g,p,u){return e(),l("div",null,a)}const m=o(t,[["render",s]]);export{h as __pageData,m as default};
